import React, { useState, useEffect } from "react";
import FormField from "../../shared/formField";
import {
  updateField,
  generateData,
  isFormValid,
  populateFields
} from "../../../utils/formActions";
import { connect } from "react-redux";
import { withRouter } from 'react-router-dom';

const UpdateSiteInfo = props => {
  const [state, setState] = useState({
    formError: false,
    formSuccess: false,
    formdata: {
      address: {
        element: "input",
        value: "",
        config: {
          name: "address_input",
          type: "text",
          placeholder: "Enter address"
        },
        validation: {
          required: true
        },
        valid: false,
        touched: false,
        validationMessage: "",
        showLabel
      },
      lastname: {
        element: "input",
        value: "",
        config: {
          name: "lastname_input",
          type: "text",
          placeholder: "Enter your lastname"
        },
        validation: {
          required: true
        },
        valid: false,
        touched: false,
        validationMessage: ""
      },
      email: {
        element: "input",
        value: "",
        config: {
          name: "email_input",
          type: "email",
          placeholder: "Enter your email"
        },
        validation: {
          required: true,
          email: true
        },
        valid: false,
        touched: false,
        validationMessage: ""
      }
    }
  });

  return (
    <div>
      Form Site Info
    </div>
  )
}

const mapStateToProps = state => {
  return {
    site: state.site
  }
}

export default connect(mapStateToProps)(withRouter(UpdateSiteInfo))
