import Carousel, { Modal, ModalGateway } from 'react-images';

class ImageLightbox extends Component {

  state = {
    modalIsOpen: false,
    // currentImage: this.props.pos,
    images: []
  }

  static getDerivedStateFromProps(props, state){
    if(props.images){
      const images = [];
      props.images.forEach(img => {
        images.push({src: `${img}`})
      });

      return state = {
        images
      }
    }

    return false;
  }

