import { LOGIN_USER, REGISTER_USER, AUTH_USER,LOGOUT_USER, ADD_TO_BASKET, GET_BASKET_ITEMS } from '../actions/types';

export default (state = {}, action) => {
  const { type, payload } = action;
  switch(type){
    case LOGIN_USER:
      return {...state, loginSuccess: payload}
    case REGISTER_USER:
      return {...state, register: payload}
    case AUTH_USER:
      return {...state, userData: payload}
    // case LOGOUT_USER:
    //   return {...state, cartData: []}
    case ADD_TO_BASKET:
      return {...state, userData: {
        ...state.userData,
        cart: payload
      }}
    case GET_BASKET_ITEMS:
      return {...state, cartData: payload}
    default:
      return state;
  }
}
