import React,{ Component, Fragment } from 'react'
import FsLightbox from 'fslightbox-react';

class ProductImg extends Component {

  state = { 
    toggler: false, 
    slide: 0,
    lightboxImages: []
    };

  componentDidMount() {
    let lightboxImages = [];
    if(this.props.detail.images.length > 0){

      this.props.detail.images.forEach(img => {
        lightboxImages.push(img.url)
      })
    }
    this.setState({
      lightboxImages
    })
  }

   renderItemImg = images => {
    if(images.length > 0){
      return images[0].url
    } else {
      return '/images/image_not_available.png'
    }
  }

   openLightboxOnSlide = pos => { 
    this.setState({
    toggler: !this.state.toggler, 
    slide: pos, 
    }); 
  }

  //  handleLightBox = pos => {
  //   if(state.lightboxImages.length > 0) {
  //     setState({
  //       ...state,
  //       lightbox: true,
  //       imagePos: pos
  //     }
  //   }
  // }

  //  handleCloseLightBox = () => {
  //   setState({
  //     ...state,
  //     lightbox: false
  //   })
  // }

   showThumbs = () => (
    this.state.lightboxImages.map((item,i) => (
      i > 0 && 
      <Fragment key={i}>
      <div
        className="thumb"
        onClick={() => this.openLightboxOnSlide(i)}
        style={{background: `url(${item}) no-repeat`}}
      >
      </div>
      <FsLightbox 
        toggler={this.state.toggler} 
        slide={this.state.slide} 
        sources={this.state.lightboxImages} 
    /></Fragment>
    ))
  )

  render() {
    const {detail} = this.props;

    return (
      <div className="product_image_container">
        <div className="main_pic">
          <div
            style={{background: `url(${this.renderItemImg(detail.images)}) no-repeat`}}
            onClick={() => this.openLightboxOnSlide(0)}
          >
          </div>
        </div>
        <div className="main_thumbs">
          {this.showThumbs()}
        </div>
      </div>
    )
  }


}

export default ProductImg
