import axios from 'axios';
import {LOGIN_USER, REGISTER_USER, AUTH_USER, LOGOUT_USER, ADD_TO_BASKET, GET_BASKET_ITEMS } from './types.js'
import { USER_SERVER, PRODUCT_SERVER } from '../../utils/misc';

export const loginUser = (dataToSubmit) => {
  const data = axios.post(`${USER_SERVER}/login`, dataToSubmit).then(res => res.data);

  return {
    type: LOGIN_USER,
    payload: data
  }
}

export const registerUser = (dataToSubmit) => {
  const data = axios.post(`${USER_SERVER}/register`, dataToSubmit).then(res => res.data);

  return {
    type: REGISTER_USER,
    payload: data
  }
}

export const authUser = () => {
  const data = axios.get(`${USER_SERVER}/auth`).then(res => res.data);

  return {
    type: AUTH_USER,
    payload: data
  }
}

export const logoutUser = () => {
  const data = axios.get(`${USER_SERVER}/logout`).then(res => res.data);

  return {
    type: LOGOUT_USER,
    payload: data
  }
}

