import React,{useState} from 'react'
import FormField from '../shared/formField';
import Dialog from '@material-ui/core/Dialog'
import{ updateField, generateData, isFormValid } from '../../utils/formActions'
import { connect } from 'react-redux'


const UpdateUserInfo = props => {

  const [state, setState] = useState({
    formError: false,
    formSuccess: false,
    formdata: {
      name: {
        element: 'input',
        value: '',
        config: {
          name: 'name_input',
          type: 'text',
          placeholder: 'Enter your name'
        },
        validation: {
          required: true,
        },
        valid: false,
        touched: false,
        validationMessage: ''
      },
      lastname: {
        element: 'input',
        value: '',
        config: {
          name: 'lastname_input',
          type: 'text',
          placeholder: 'Enter your lastname'
        },
        validation: {
          required: true,
        },
        valid: false,
        touched: false,
        validationMessage: ''
      },
      email: {
        element: 'input',
        value: '',
        config: {
          name: 'email_input',
          type: 'email',
          placeholder: 'Enter your email'
        },
        validation: {
          required: true,
          email: true
        },
        valid: false,
        touched: false,
        validationMessage: ''
      }
    }
  })


  const handleChange = el => {
    const formdata = updateField(el, state.formdata, 'update_user');
    setState({
      ...state,
      formError: false,
      formdata
    })
  }

  const handleSubmit = e => {
    e.preventDefault();

    let dataToSubmit = generateData(state.formdata, 'update_user');
    let formIsValid = isFormValid(state.formdata, 'update_user');

    if(formIsValid){
     console.log(dataToSubmit)
    } else {
      setState({
        ...state,
        formError: true
      })
    }
  }

  return (
    <div>
      <form onSubmit={e=>handleSubmit(e)}></form>
    </div>
  )
}

export default UpdateUserInfo
