import axios from 'axios';
import { GET_SITE_INFO } from './types.js'
import { SITE_SERVER } from '../../utils/misc';

export const getSiteInfo = () => {
  const data = axios.get(`${SITE_SERVER}/site_data`).then(res => res.data);

  return {
    type: GET_SITE_INFO,
    payload: data
  }
 }
