import React,{ useState, useEffect, Fragment } from 'react'
import Lightbox from 'react-images';

const ProductImg = props => {

  const [state, setState] = useState({ 
    lightboxIsOpen: false,
    currentImage: 0,
    images: []
    });

  useEffect(() => {
    let images = [];
    if(props.detail.images.length > 0){

      props.detail.images.forEach(item => {
        images.push({src: `${item.url}`})
      })
    }
    setState({
      ...state,
      images
    })
  }, [props.detail])

  const renderItemImg = images => {
    if(images.length > 0){
      return images[0].url
    } else {
      return '/images/image_not_available.png'
    }
  }

  // const toggleModal = () => { 

  //   setLightboxController({
  //   ...lightboxController,
  //   modalIsOpen: !lightboxController.modalIsOpen, 
  //   }); 
  //   }

  const openLightBox = pos => {
    if(state.lightboxImages.length > 0) {
      setState({
        ...state,
        lightboxIsOpen: true,
        currentImage: pos
      })
    }
  }

  const closeLightBox = () => {
    setState({
      ...state,
      lightboxIsOpen: false
    })
  }

  // const openLightbox = () => {
  //   if(lightboxController.lightboxImages.length > 0){
  //     setLightboxController({
  //       ...lightboxController,
  //       lightbox: true
  //     })
  //   }
  // }

  const showThumbs = () => (
    state.images.map((item,i) => (
      i > 0 && 
      <div
        key={i}
        className="thumb"
        onClick={() => openLightBox(i)}
        style={{background: `url(${item.src}) no-repeat`}}
      >
      </div>  
    ))
  )


  const {detail} = props;
  // console.log(lightboxController.lightboxImages)

  return (
    <div className="product_image_container">
      <div className="main_pic">
        <div
          style={{background: `url(${renderItemImg(detail.images)}) no-repeat`}}
          onClick={() => openLightBox(0)}
        >
        </div>
      </div>
      <div className="main_thumbs">
      {showThumbs()}
      </div>{
        state.images > [] &&
        <Lightbox
        currentImage={state.currentImage}
        images={state.images}
        isOpen={state.lightboxIsOpen}
        // onClickPrev={() => gotoPrevious()}
        // onClickNext={() => gotoNext()}
        onClose={() => closeLightBox()}
      />
      }

    </div>
  )
}

export default ProductImg
