  }

  const calculateTotal = cartData => {
    if(cartData.length > 0){
        let total = 0;

        cartData.forEach(item => {
          total += parseInt()
        })

    }
  }

  return (
    <UserLayout>
      <div>
        <h1>My Basket</h1>
        <div className="user_cart">
          <CartBlock
            products={props.user}
            type="cart"
            removeItem={id => removeFromCart(id)}
          />
        </div>
      </div>
    </UserLayout>
  );
};

const mapStateToProps = state => {
  return {
    user: state.user
  };
};

export default connect(mapStateToProps)(UserCart);
