import PageTop from '../shared/pageTop';
import ProductInfo from './productInfo';
import { connect } from 'react-redux';
import { getProductById, clearProductDetail } from '../../store/actions/productActions'
import CircularProgress from '@material-ui/core/CircularProgress';

const ProductPage = props => {

  useEffect(()=> {
    const id = props.match.params.id;
    props.dispatch(getProductById(id));
    return () => props.dispatch(clearProductDetail());
  }, [])

  const handleAddToCart = id => {

  }

  return (
    <div>
      <PageTop
        title="Product detail"
      />
      <div className="container">
        {
          props.product.item ?
            <div className="product_detail_wrapper">
              <div className="left">
                images
              </div>
              <div className="right">
                <ProductInfo
                  detail={props.product.item}
                  addToCart={id => handleAddToCart(id)}               
                />
              </div>
            </div>
          : <CircularProgress
              style={{ color: '#00bcd5' }} 
              thickness={7}
            />

        }
      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    product: state.product
  };
};

export default connect(mapStateToProps)(ProductPage)
