import React,{ useState, useEffect, Fragment } from 'react'
import FsLightbox from 'fslightbox-react';
import { connect } from 'react-redux';
import { createSlide, clearSlide } from '../../store/actions/productActions'

const ProductImg = props => {

  const [lightboxController, setLightboxController] = useState({ 
    toggler: false, 
    slide: 1,
    lightboxImages: []
    });

  useEffect(() => {
    if(props.detail.images.length > 0){
     props.dispatch(createSlide(props.detail.images))}
  }, [props.detail.images])

  setLightboxController({
    ...lightboxController,
    lightboxImages: props.slide
  })

  const renderItemImg = images => {
    if(images.length > 0){
      return images[0].url
    } else {
      return '/images/image_not_available.png'
    }
  }

  const openLightboxOnSlide = pos => { 
    setLightboxController({
    ...lightboxController,
    toggler: !lightboxController.toggler, 
    slide: pos,
    }); 
    }

  // const handleLightBox = pos => {
  //   if(state.lightboxImages.length > 0) {
  //     setState({
  //       ...state,
  //       lightbox: true,
  //       imagePos: pos
  //     }
  //   }
  // }

  // const handleCloseLightBox = () => {
  //   setState({
  //     ...state,
  //     lightbox: false
  //   })
  // }

  const showThumbs = () => (
    props.slide && props.slide.map((item,i) => (
      i > 0 && 
      <Fragment key={i}>
      <div
        className="thumb"
        onClick={() => openLightboxOnSlide(i+1)}
        style={{background: `url(${item}) no-repeat`}}
      >
      </div>
      <FsLightbox 
        toggler={lightboxController.toggler} 
        slide={lightboxController.slide} 
        sources={lightboxController.lightboxImages} 
    /></Fragment>
    ))
  )

  const {detail} = props;
  console.log(lightboxController.lightboxImages)

  return (
    <div className="product_image_container">
      <div className="main_pic">
        <div
          style={{background: `url(${renderItemImg(detail.images)}) no-repeat`}}
          onClick={() => openLightboxOnSlide(1)}
        >
        </div>
      </div>
      <div className="main_thumbs">
        {showThumbs()}

      </div>
    </div>
  )
}

const mapStateToProps = state => {
  return {
    slide: state.product.slide
  };
};

export default connect(mapStateToProps)(ProductImg)
