import React,{ useState, useEffect } from 'react'

const ProductImg = props => {
  const [state, setState] = useState({
    lightbox: false,
    imagePos: 0,
    lightboxImages: []
  })

  useEffect(() => {
    if(props.detail.images.length > 1){
      let lightboxImages = [];

      props.detail.images.forEach(item => {
        lightboxImages.push(item.url)
      })

      setState({
        ...state,
        lightboxImages
      })
    }
  }, [])

  const renderItemImg = images => {
    if(images.length > 0){
      return images[0].url
    } else {
      return '/images/image_not_available.png'
    }
  }

  const handleLightBox = () => {

  }

  const showThumbs = product => (
    state.lightboxImages.map((item,i) => (
      i > 0 && 
      <div
        key={i}
        onClick={() => handleLightBox(i)}
      >
      </div>
    ))
  )

  const {detail} = props;

  return (
    <div className="product_image_container">
      <div className="main_pic">
        <div
          style={{background: `url(${renderItemImg(detail.images)}) no-repeat`}}
          onClick={() => handleLightBox(0)}
        >

        </div>
      </div>
      <div className="main_thumbs">
        { showThumbs(detail)}
      </div>
    </div>
  )
}

export default ProductImg
