import React,{ useState, useEffect } from 'react'
import ImageLightbox from '../shared/lightbox'

const ProductImg = props => {
  const [state, setState] = useState({
    lightbox: false,
    imagePos: 0,
    lightboxImages: []
  })

  useEffect(() => {
    let lightboxImages = [];
    if(props.detail.images.length > 0){

      props.detail.images.forEach(item => {
        lightboxImages.push(item.url)
      })
    }
    setState({
      ...state,
      lightboxImages
    })
  }, [props.detail.images])

  const renderItemImg = images => {
    if(images.length > 0){
      return images[0].url
    } else {
      return '/images/image_not_available.png'
    }
  }

  const handleLightBox = pos => {
    if(state.lightboxImages.length > 0) {
      setState({
        ...state,
        lightbox: true,
        imagePos: pos
      })
    }
  }

  const handleCloseLightBox = () => {
    setState({
      ...state,
      lightbox: false
    })
  }

  const showThumbs = () => (
    state.lightboxImages.map((item,i) => (
      i > 0 && 
      <div
        className="thumb"
        key={i}
        onClick={() => handleLightBox(i)}
        style={{background: `url(${item}) no-repeat`}}
      >
      </div>
    ))
  )

  const {detail} = props;

  return (
    <div className="product_image_container">
      <div className="main_pic">
        <div
          style={{background: `url(${renderItemImg(detail.images)}) no-repeat`}}
          onClick={() => handleLightBox(0)}
        >

        </div>
      </div>
      <div className="main_thumbs">
        {showThumbs()}
      </div>
      {
        state.lightbox && 
        <ImageLightbox 
          id={detail.id}
          images={state.lightboxImages}
          open={state.lightbox}
          pos={state.imagePos}
          onclose={() => handleCloseLightBox()}
        />
      }
    </div>
  )
}

export default ProductImg
